void cfm_hwmodel::revert_frame() {

	/* Reverting the image means that Pixel 0 becomes Pixel N-1, where N is the frame size (in Pixels)
	 * In YUV 422 format, it implies to:
	 *   - Revert the Y table, located at offset 0
	 *   - Revert the U table, located at offset N
	 *   - Revert the V table, located at offset N*1.5
	 */
	unsigned char* outpayload = outputFrame.payload;
	unsigned char* inpayload = inputFrame.payload;

	int offsetU = (int) (NbPixelsPerLineMax * NbLinesMax);
	int offsetV = (int) (NbPixelsPerLineMax * NbLinesMax * 1.5);

	int pixel_block_idx = 0;

	int line_idx;

	int current_block_ofs = 0;

	for (line_idx = 0; line_idx < NbLinesMax; line_idx++) {
		for (pixel_block_idx = 0; pixel_block_idx < NbPixelsPerLineMax / 4; pixel_block_idx++) {

			outpayload[offsetU - 1 - current_block_ofs] = inpayload[current_block_ofs + 0];
			outpayload[offsetU - 1 - current_block_ofs - 1] = inpayload[current_block_ofs + 1];
			outpayload[offsetU - 1 - current_block_ofs - 2] = inpayload[current_block_ofs + 2];
			outpayload[offsetU - 1 - current_block_ofs - 3] = inpayload[current_block_ofs + 3];

			outpayload[offsetV - 1 - current_block_ofs / 2 - 0] = inpayload[offsetU + current_block_ofs / 2 + 0];
			outpayload[offsetV - 1 - current_block_ofs / 2 - 1] = inpayload[offsetU + current_block_ofs / 2 + 1];

			outpayload[NbPixelsPerLineMax * NbLinesMax * 2 - 1 - current_block_ofs / 2 - 0] = inpayload[offsetV + current_block_ofs / 2 + 0];
			outpayload[NbPixelsPerLineMax * NbLinesMax * 2 - 1 - current_block_ofs / 2 - 1] = inpayload[offsetV + current_block_ofs / 2 + 1];

			current_block_ofs += 4;

		}
	}
}

