void cfm_bt_system::cb_init_attributes() {

	mq_Data.cfa_write_time.init(cf_expr_duration(1, CF_NS));
	mq_Data.cfa_read_time.init(cf_expr_duration(1, CF_NS));
	mq_Data.cfa_semaphore.init(false);
	mq_Data.cfa_concurrency.init((cf_nonzero_count) 1);
	mq_DataIn.cfa_send_time.init(cf_expr_duration(1, CF_NS));
	mq_DataIn.cfa_receive_time.init(cf_expr_duration(1, CF_NS));
	mq_DataIn.cfa_queue_policy.init(CF_MQ_POLICY_FIFO_FINITE);
	mq_DataIn.cfa_queue_capacity.init((cf_nonzero_count) 1);
	mq_DataIn.cfa_concurrency.init((cf_nonzero_count) 1);
	mq_DataIn.cfa_send_threshold.init((cf_nonzero_count) 1);
	mq_DataIn.cfa_receive_threshold.init((cf_nonzero_count) 1);
	mq_DataOut.cfa_send_time.init(cf_expr_duration(1, CF_NS));
	mq_DataOut.cfa_receive_time.init(cf_expr_duration(1, CF_NS));
	mq_DataOut.cfa_queue_policy.init(CF_MQ_POLICY_FIFO_FINITE);
	mq_DataOut.cfa_queue_capacity.init((cf_nonzero_count) 1);
	mq_DataOut.cfa_concurrency.init((cf_nonzero_count) 1);
	mq_DataOut.cfa_send_threshold.init((cf_nonzero_count) 1);
	mq_DataOut.cfa_receive_threshold.init((cf_nonzero_count) 1);
	ev_WrOK.cfa_set_time.init(cf_expr_duration(1, CF_NS));
	ev_WrOK.cfa_get_time.init(cf_expr_duration(1, CF_NS));
	ev_WrOK.cfa_event_policy.init(CF_EV_POLICY_BOOLEAN);

	return;
}

